import React, { useState } from 'react';
import Modal from '../Modal/Modal';


const Search = () => {
const [value,setValue]=useState();
const [books,setBooks]=useState();
   const getInputValue=(e)=>{
setValue(e.target.value);

    }

    // AIzaSyAjn5antfyutAbFQGoOfDjWETTY20_7WxM
    
        const fetchData=()=>{
            const url = `https://www.googleapis.com/books/v1/volumes?q=${value}&key=AIzaSyAjn5antfyutAbFQGoOfDjWETTY20_7WxM`

            // fetch the url
          
            fetch(url)
              .then(res => res.json())
              .then(data => setBooks(data.items))
            
            
        }


    return (
        <div> 
        <div className='mx-auto'>
            <h1 className='text-white fw-bold mt-5 bg-white text-center bg-opacity-25 p-5 mx-auto rounded-5' style={{width:"25%"}}>F<span className='text-danger'>!</span>nd Knowledge</h1>
        </div>
            <div className="input-group mt-5 pt-5 w-50 mx-auto bg-white bg-opacity-25 p-5 rounded shadow-lg">
                <input onChange={getInputValue}type="text" id="search-field"
                    className="form-control bg-dark bg-opacity-50 text-white border border-white rounded border-2 shadow-lg"
                    placeholder="search your book here" aria-label="Recipient's username"
                    aria-describedby="button-addon2"/>
                <button
                onClick={fetchData}
                    className="btn btn-success ms-2 border border-success rounded border-2" type="button"
                    id="button-addon2">Search</button>
            </div>

            <div className="row gy-4 cards">
            {

                books && books.map((book)=>{
                
<div class="col-4">
    <div class="card h-100 p-3 bg-black text-white border border-2 border-white">
      <img src={book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
      <div class="card-body">
        <div className='text-left'>
        <h3 class="card-title">{book.volumeInfo.title}</h3>
        <p class="card-title text-warning fw-bold">{book.volumeInfo.authors}</p>
        <p class="card-title"><span className='fw-bold'>Publisher: </span>{book.volumeInfo.publisher}</p>
        <p class="card-title fw-bold">Publish Year:<span class=" text-success fs-5">{book.volumeInfo.publishedDate}</span></p>
        </div>
        <div >
        <h2 className='text-end text-danger'>{book.volumeInfo.pageCount} Tk</h2>
        </div>
        
      </div>

      <div>
    </div>
    </div>
    <Modal
    book="book"></Modal>
  </div>

                 } )
            }
        </div>

            
        </div>
    );
};

export default Search;
