import React, { useState } from 'react';

const Modal = ({show,book}) => {
  const [name,setName]=useState('');
  const [address,setAddress]=useState('');
  const [email,setEmail]=useState('');









   if(!show){
    return null;
   }
   console.log(book)
    return (
        <div>


      {/* Confirm Modal  */}
 {/*
 language
 categories

 */}


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog  modal-dialog-scrollable">
    <div class="modal-content bg-black text-white border border-2 border-white">
        <div class="modal-header text-white">
        <img src={book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
          
          <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body text-white">
        <h1 class="modal-title fs-2 fw-bold" id="exampleModalLabel">{book.volumeInfo.title}</h1>
          <h3 class="modal-title" id="exampleModalLabel"><span className='fs-5'>Catagory:</span>{book.volumeInfo.categories}</h3>
          <h3 class="modal-title" id="exampleModalLabel"><span className='fs-5'>Language:</span>{book.volumeInfo.language}</h3>
          
        </div>
        <div class="modal-footer">
              <form class="row gy-3 form-group" onSubmit={handleSubmit}>
                    <div class="col-12">
                      <input onChange={(e)=>setName(e.target.value)} value={name} type="text" class="form-control" placeholder="Full Name" aria-label="Full Name"/>
                    </div>
                    <div class="col-12">
                      <input onChange={(e)=>setAddress(e.target.value)} value={address} type="text" class="form-control" placeholder="Address" aria-label="Address"/>
                    </div>
                    <div class="col-12">
                      <input onChange={(e)=>setEmail(e.target.value)} value={email} type="email" class="form-control" placeholder="E-mail" aria-label="email"/>
                    </div>
                    <button type="submit" class="btn btn-primary " style={{width:"95%"}}>Submit</button>
              </form>
          
          
          
          
        </div>
      
      
                  
      
    </div>
  </div>
</div>
        </div>
    );
};

export default Modal;
