import React, { useState } from 'react';


const Search = () => {
const [value,setValue]=useState();
const [books,setBooks]=useState();
   const getInputValue=(e)=>{
setValue(e.target.value);

    }

    // AIzaSyAjn5antfyutAbFQGoOfDjWETTY20_7WxM
    
        const fetchData=()=>{
            const url = `https://www.googleapis.com/books/v1/volumes?q=${value}&key=AIzaSyAjn5antfyutAbFQGoOfDjWETTY20_7WxM`

            // fetch the url
          
            fetch(url)
              .then(res => res.json())
              .then(data => setBooks(data.items))
            
            
        }


    return (
        <div> 
        <div className='mx-auto'>
            <h1 className='text-white fw-bold mt-5 bg-white text-center bg-opacity-25 p-5 mx-auto rounded-5' style={{width:"25%"}}>F<span className='text-danger'>!</span>nd Knowledge</h1>
        </div>
            <div className="input-group mt-5 pt-5 w-50 mx-auto bg-white bg-opacity-25 p-5 rounded shadow-lg">
                <input onChange={getInputValue}type="text" id="search-field"
                    className="form-control bg-dark bg-opacity-50 text-white border border-white rounded border-2 shadow-lg"
                    placeholder="search your book here" aria-label="Recipient's username"
                    aria-describedby="button-addon2"/>
                <button
                onClick={fetchData}
                    className="btn btn-success ms-2 border border-success rounded border-2" type="button"
                    id="button-addon2">Search</button>
            </div>

            <div className="row gy-4 cards">
            {

                books && books.map(book=>{

                    let thumbnail= book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail;
                   let amount=book.saleInfo.listPrice && book.saleInfo.listPrice.amount;
                    if(thumbnail!==undefined && amount!==undefined){
                // Organizing Cards

<div class="col-4">
    <div class="card h-100 p-3 bg-black text-white border border-2 border-white">
      <img src={thumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
      <div class="card-body">
        <div className='text-left'>
        <h3 class="card-title">{book.volumeInfo.title}</h3>
        <p class="card-title text-warning fw-bold">{book.volumeInfo.authors}</p>
        <p class="card-title"><span className='fw-bold'>Publisher: </span>{book.volumeInfo.publisher}</p>
        <p class="card-title fw-bold">Publish Year:<span class=" text-success fs-5">{book.volumeInfo.publishedDate}</span></p>
        </div>
        <div >
        <h2 className='text-end text-danger'>{amount} Tk</h2>
        </div>
        
      </div>
      <div className='d-flex justify-content-center'>
      <button type="button" class="btn btn-success" data-bs-toggle="modal" data-bs-target="#exampleModal">
         Confirm Order
    </button>
      </div>


      <div>


{/* Confirm Modal Button */}

      
 {/* Confirm Modal  */}
 {/*
 language
 categories

 */}


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header ">
      <img src={thumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
        
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body text-dark">
      <h1 class="modal-title fs-2 fw-bold" id="exampleModalLabel">{book.volumeInfo.title}</h1>
        <h3 class="modal-title" id="exampleModalLabel"><span>Catagory:</span> {book.volumeInfo.categories}</h3>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
      </div>
    </div>
  </div>

}})
            }
        </div>

            
        </div>
    );
};

export default Search;
