import React from 'react';

const Modal = ({show,book}) => {
   if(!show){
    return null;
   }
   console.log(book)
    return (
        <div>


      {/* Confirm Modal  */}
 {/*
 language
 categories

 */}


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog  modal-dialog-scrollable">
    <div class="modal-content row" style={{width:"90vh"}}>
        <div class="modal-header ">
        <img src={book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
          
          <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body text-dark">
        <h1 class="modal-title fs-2 fw-bold" id="exampleModalLabel">{book.volumeInfo.title}</h1>
          <h3 class="modal-title" id="exampleModalLabel"><span className='fs-5'>Catagory:</span>{book.volumeInfo.categories}</h3>
          <h3 class="modal-title" id="exampleModalLabel"><span className='fs-5'>Language:</span>{book.volumeInfo.language}</h3>
          
        </div>
        <div class="modal-footer">
        <div class="row">
              <div class="col-12">
                <input type="text" class="form-control" placeholder="First name" aria-label="First name"/>
              </div>
              <div class="col-12">
                <input type="text" class="form-control" placeholder="Last name" aria-label="Last name"/>
              </div>
            </div>
          
          <button type="button" class="btn btn-primary">Submit</button>
        </div>
      
      
                  
      
    </div>
  </div>
</div>
        </div>
    );
};

export default Modal;
