import React from 'react';

const Card = ({books}) => {




    return (
        <>
        <div className="row gy-4 cards">
        {
            books.map((book)=>{
                return(

            <div class="col-4">
    <div class="card h-100 p-3 bg-black text-white border border-2 border-white">
      <img src={book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
      <div class="card-body">
        <div className='text-left'>
        <h3 class="card-title">{book.volumeInfo.title}</h3>
        <p class="card-title text-warning fw-bold">{book.volumeInfo.authors}</p>
        <p class="card-title"><span className='fw-bold'>Publisher: </span>{book.volumeInfo.publisher}</p>
        <p class="card-title fw-bold">Publish Year:<span class=" text-success fs-5">{book.volumeInfo.publishedDate}</span></p>
        </div>
        <div >
        <h2 className='text-end text-danger'>{book.volumeInfo.pageCount} Tk</h2>
        </div>
        
      </div>

      <div>
    </div>
    </div>
  </div>
        
                )
            })
        }
        </div>
        </>
    );
};

export default Card;
