import React from 'react';

const Modal = ({show,book}) => {
   if(!show){
    return null;
   }
    return (
        <div>


      {/* Confirm Modal  */}
 {/*
 language
 categories

 */}


<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header ">
      <img src={book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail} style={{height:"500px"}} class="card-img-top img-fluid" alt="..."/>
        
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body text-dark">
      <h1 class="modal-title fs-2 fw-bold" id="exampleModalLabel"></h1>
        <h3 class="modal-title" id="exampleModalLabel"><span>Catagory:</span></h3>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
        </div>
    );
};

export default Modal;
